--SF6 Costume and Color Unlocker--By alphaZomega--Sept 27, 2023local costume_list = sdk.create_userdata("app.FighterCostumeUserData", "product\\cfncontents\\onlineshop\\fightercostumeuserdata.user"):add_ref()local color_list = sdk.create_userdata("app.FighterCostumeColorUserData", "product\\cfncontents\\onlineshop\\fightercostumecoloruserdata.user"):add_ref()local function generate_unlock_array(userdata)        local unlocked_list = {}    for i, item in pairs(userdata.DataArray) do        if not item.IsDefault then            table.insert(unlocked_list, item.id)        end    end    local array = sdk.create_managed_array("System.UInt32", #unlocked_list):add_ref()    for i, int in ipairs(unlocked_list) do         array[i-1] = int     end        return arrayendre.on_draw_ui(function()        if imgui.tree_node("SF6 Unlocker") then        imgui.begin_rect()                local save_mgr = sdk.get_managed_singleton("app.SystemSaveManager")                if imgui.button("Unlock Costumes") then            save_mgr.Data.CostumeList = generate_unlock_array(costume_list)            re.msg("Unlocked all Fighting Ground costumes")        end                if imgui.button("Unlock Colors") then            save_mgr.Data.ColorList = generate_unlock_array(color_list)            re.msg("Unlocked all Fighting Ground colors")        end                if imgui.tree_node("Save Data") then            if EMV then                imgui.managed_object_control_panel(save_mgr.Data)            else                object_explorer:handle_address(save_mgr.Data)            end            imgui.tree_pop()        end                imgui.end_rect(2)        imgui.tree_pop()    end    end)